import React from 'react';
import * as firebase from 'firebase';
import './App.css';


export default class Login extends React.Component{
    componentDidMount(){
        document.getElementsByClassName("signedOutContainerLeft")[0].style.height = window.innerHeight + 'px';

        setTimeout(()=>{document.getElementsByClassName("signedOutContainerLeft")[0].classList.toggle("signedOutContainerLeftShowing")},1400);
        this.checkLoggedIn();
    }

    checkLoggedIn(){
        var user = localStorage.getItem("oc_loggedIn");
        if(user){
            window.location.href = "/home";
        }else{
            //window.location.href = "/login";
        }
    }



    render(){
        return(

            <table className="loginParent">
            <tr>
            <td width="50%">
            <div className="signedOutContainerLeft">
            <br/>
            <h2 id="welcome-text">Welcome.</h2><br/>
            <p id="welcome-small-text">Onecab is the fresh, new and affordable.
            By keeping our operations lean, small and agile we save you money on every journey that you take with us.</p><br/>

            <fieldset className="registrationPanel">
            <center>
            <p id="smallTitle">
            Create your account
            </p><br/>
            <input type="number" id="cellphone" max-length="10" placeholder="0621232567"/><br/>
            <button id="submitLogin">Continue</button>
            </center>
            </fieldset>
            </div>
            </td>

            <td width="50%" valign="top">
            <div className="signedOutContainerRight">
            <br/>
            <h2 id="welcome-text">Welcome.</h2><br/>
            <p id="welcome-small-text">Onecab is the fresh, new and affordable.
            By keeping our operations lean, small and agile we save you money on every journey that you take with us.</p><br/>

            <fieldset className="registrationPanel">
            <center>
            <p id="smallTitle">
            Verify your phone number
            </p><br/>
            <input type="number" id="cellphone" max-length="10" placeholder="Verification PIN"/><br/>
            <button id="submitLogin">Continue</button>
            </center>
            </fieldset>
            </div>
            </td>

            </tr>
            </table>
        );
    }

}
