import React from 'react';
import * as firebase from 'firebase';
import './App.css';


export default class Login extends React.Component{
    componentDidMount(){
        document.getElementsByClassName("signedOutContainer")[0].style.height = window.innerHeight + 'px';
        document.getElementsByClassName("signedOutContainer")[0].classList.toggle("signedOutContainerShowing")
        this.checkLoggedIn();
    }

    checkLoggedIn(){
        var user = localStorage.getItem("oc_loggedIn");
        if(user){
            window.location.href = "/home";
        }else{
            //window.location.href = "/login";
        }
    }



    render(){
        return(
            <div className="signedOutContainer">
            <br/>
            <h2 id="welcome-text">Welcome.</h2><br/>
            <p id="welcome-small-text">Onecab is the fresh, new and affordable
            By keeping our operations lean, small and agile we save you money on every journey that you take with us.</p><br/>

            <fieldset className="registrationPanel">
            <center>
            <p id="smallTitle">
            Create your account
            </p><br/>
            <input type="number" id="cellphone" max-length="10" placeholder="0621232567"/><br/>
            <button id="submitLogin">Continue</button>
            </center>
            </fieldset>
            </div>
        );
    }

}
